        private DateTime playedDate;
        public DateTime PlayedDate
        {
            get
            {          
                return playedDate;
            }

            set
            {
                if (playedDate > DateTime.MinValue)
                {
                    var dt = (value - playedDate).TotalHours;
                    if (dt > 2 || dt < -2)
                        playedDate = value;
                    else
                        return; 

                }
                playedDate = value; 
            }
        }
