        public void import(TrackInfoItunes db)
        {
            this.albumRating = db.albumRating;
            this.rating = db.rating;
            this.rare = db.rare;
            this.info = db.info;
            this.playCount = db.playCount;
            this.playedDate = db.playedDate;
            this.enabled = db.enabled;
            present = true;
            db.present = true;

            import();
        }

