namespace pdb.player.ViewModel
{
    public class BibViewModelBase : ViewModelBase
    {

        protected readonly BibViewModelBase _parent;
        #region Constructors
        private object model;
        public BibViewModelBase(object model, BibViewModelBase parent)
        {
            this.model = model;
            this._parent = parent;
        }

        public BibViewModelBase(BibViewModelBase parent)
        {
            this._parent = parent;
        }

        #endregion

        #region Presentation Members

        bool _isExpanded;
        protected bool _isSelected;
        bool _isChecked;
        bool _isEnabled = true;


        #region IsExpanded

        /// <summary>
        /// Gets/sets whether the TreeViewItem 
        /// associated with this object is expanded.
        /// </summary>
        public bool IsExpanded
        {
            get { return _isExpanded; }
            set
            {
                if (value != _isExpanded)
                {
                    _isExpanded = value;
                    this.OnPropertyChanged("IsExpanded");
                    if (_parent != null && _isExpanded)
                        _parent.IsExpanded = true;
                }


            }
        }

        #endregion // IsExpanded

        #region selection

        /// <summary>
        /// Gets/sets whether the TreeViewItem 
        /// associated with this object is selected.
        /// </summary>
        public virtual bool IsSelected
        {
            get { return _isSelected; }
            set
            {
                if (value != _isSelected)
                {
                    _isSelected = value;
                    this.OnPropertyChanged("IsSelected");
                    //if (_isSelected)
                    //    IsExpanded = true; 
                    // SelectedItem = _isSelected ? this : null;
                }
            }
        }





        #endregion // IsSelected

        #region checked

        public bool IsChecked
        {
            get { return _isChecked; }
            set
            {
                if (value != _isChecked)
                {
                    _isChecked = value;
                    this.OnPropertyChanged("IsChecked");
                }


            }
        }
        #endregion


        #region enabled

        public bool IsEnabled
        {
            get { return _isEnabled; }
            set
            {
                if (value != _isEnabled)
                {
                    _isEnabled = value;
                    this.OnPropertyChanged("IsEnabled");
                }


            }
        }
        #endregion

        #endregion // Presentation Members

        public override string ToString()
        {
            if (model == null)
                return base.ToString();
            return model.ToString();
        }

        //public virtual void refresh()
        //{
        //    OnPropertyChanged(""); 
        //}
    }
}

using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Windows.Media;

namespace pdb.player.ViewModel.Colors
{
    public delegate bool colorvm(ColorViewModel vm);

    /// <summary>
    /// Paramètre de couleur
    /// </summary>
    public class ColorViewModel : BibViewModelBase, IEnumerable<ColorViewModel>
    {
        /// <summary>
        /// Nom du paramètre
        /// </summary>
        private string name;
        /// <summary>
        /// Code couleur
        /// </summary>
        private string code;

        public readonly string defaultCode;

        public string Description { get; private set; }

        private static ColorViewModel selected;

        public ColorViewModel Selected
        {
            get
            {
                return selected;
            }

            set
            {
                if (value != selected)
                {
                    selected = value;
                    OnPropertyChanged("Selected"); 
                }
            }
        }

        public override bool IsSelected
        {
            get
            {
                return base.IsSelected;
            }
            set
            {
                base.IsSelected = value;
                if (value)
                    Selected = this;
            }
        }




        public ColorViewModel Parent { get { return _parent as ColorViewModel; } }
        protected List<ColorViewModel> subs = new List<ColorViewModel>();


        public ColorViewModel(string name, string description, ColorViewModel parent)
            : base(parent)
        {
            this.name = name;
            this.Description = description;
            if (parent != null)
                parent.subs.Add(this);
        }

        public ColorViewModel(string name, string description, string defaultCode, ColorViewModel parent)
            : this(name, description, parent)
        {

            this.defaultCode = defaultCode;
            this.Code = defaultCode;

        }

        public string Name { get { return name; } }
        public string Code
        {
            get
            {
                return code;
            }
            set
            {
                if (value != code)
                {
                    code = value;
                    _color = default(Color);
                    _brush = null;
                    OnPropertyChanged("Code");
                    OnPropertyChanged("ArboCode");
                    OnPropertyChanged("Color");
                    OnPropertyChanged("Brush");
                }
            }
        }

        public string ArboCode
        {
            get
            {
                if (string.IsNullOrEmpty(code))
                {
                    var parent = Parent;
                    if (parent != null)
                        return Parent.ArboCode;
                }
                return code;
            }
        }

        public string ArboName
        {
            get
            {
                var parent = Parent;
                if (parent == null || string.IsNullOrEmpty(parent.Name))
                    return name;
                return parent.ArboName + "." + name;
            }
        }

        public void reset()
        {
            Code = defaultCode;
        }
        public void resetTree()
        {
            reset();
            foreach (ColorViewModel sub in subs)
                sub.reset();
        }

        public bool tree(colorvm action)
        {
            if (action(this))
                return true;
            foreach (ColorViewModel sub in subs)
            {
                if (sub.tree(action))
                    return true;
            }
            return false;
        }




        public IEnumerator<ColorViewModel> GetEnumerator()
        {
            return subs.GetEnumerator();
        }

        System.Collections.IEnumerator System.Collections.IEnumerable.GetEnumerator()
        {
            return subs.GetEnumerator();
        }

        private Color _color;
        public Color Color
        {
            get
            {
                if (_color == default(Color))
                {
                    _color = (Color)ColorConverter.ConvertFromString(ArboCode);
                }
                return _color;
            }
        }

        private SolidColorBrush _brush;
        public SolidColorBrush Brush
        {
            get
            {
                if (_brush == null)
                {
                    _brush = new SolidColorBrush(Color);
                }
                return _brush;
            }
        }
    }
}

