    public class TrackColor : ColorViewModel
    {

        private ColorViewModel selected; public ColorViewModel Selected { get { return selected; } }
        private ColorViewModel moved; public ColorViewModel Moved { get { return moved; } }
        private ColorViewModel next; public ColorViewModel Next { get { return next; } }
        private ColorViewModel nextAbsent; public ColorViewModel NextAbsent { get { return nextAbsent; } }
        public readonly Export Export;

        public TrackColor(ColorViewModel parent)
            : base("track", "couleur des morceaux", parent)
        {
            selected = new ColorViewModel("selected", "morceau sélectionné", "#3399ff", this);
            moved = new ColorViewModel("moved",  "morceau déplacé pour éviter répétition (quarantaine)","#93c8ce", this);
            next = new ColorViewModel("next","prochain morceau dans l'album en mode automatique", "#b5eaf0", this);
            nextAbsent = new ColorViewModel("nextAbsent","prochain morceau dans l'album en mode automatique malheureusement absent", "#c4dfe3", this);
            Export = new Export(this); 
        }
    }

    public class Export : ColorViewModel
    {
        public readonly ColorViewModel none;
        public readonly ColorViewModel miss;
        public readonly ColorViewModel present;
        public readonly ColorViewModel copy;
        public readonly ColorViewModel delete;
        public Export(ColorViewModel parent)
            : base("export","statut export", parent)
        {
            none = new ColorViewModel("none", "pas de statut", "#FFFFFFFF", this);
            miss = new ColorViewModel("miss","absent", "#FFD3D3D3", this);
            present = new ColorViewModel("present", "présent","#FFFFFFFF", this);
            copy = new ColorViewModel("copy", "le morcveau a été copié","#FF90EE90", this);
            delete = new ColorViewModel("delete", "le morceau a été supprimé","#FFA9A9A9", this);
        }
    }
}

using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.IO;

namespace pdb.player.ViewModel.Colors
{
    class MementoColor
    {
        const string PATH = "colors.txt";
        public static Couleurs Arbo = Couleurs.Instance; 
        public static void save()
        {
            var sb = new StringBuilder();
            Arbo.tree((vm) =>
            {
                if (vm.Parent == null)
                    return false; 
                sb.Append(vm.ArboName);
                sb.Append("=");
                sb.AppendLine(vm.Code);
                return false; 
            });

            var content = sb.ToString();
            File.WriteAllText(PATH, content); 

        }

        public static void recover()
        {
            try
            {
                var lines = File.ReadAllLines(PATH);
                foreach (string line in lines)
                {
                    if (string.IsNullOrEmpty(line))
                        continue;
                    var tab = line.Split('=');
                    if (tab.GetLength(0) != 2)
                        continue;

                    Arbo.tree((vm) =>
                    {
                        if (vm.ArboName == tab[0])
                        {
                            vm.Code = tab[1];
                            return true;
                        }
                        return false;
                    });
                }
            }
            catch
            {
            }
        }

    }
}

