        const string PATH_DYN_DIFF = "..\\dynamic_diff";

        const decimal EPSILON = 0.001m;
        const int NB_HISTO = 1000000;
        public int safe;
        public decimal coeffVirt = 1m;

        //private bool firstSave = true;
