            write("serial", _nbSameDirection);
            //writeStart("histo");
            //foreach (decimal h in histo)
            //{
            //    w.WriteStartElement("h");
            //    w.WriteString(h.ToString("0.###"));
            //    w.WriteEndElement();
            //}
            //writeEnd();

            var sb = new StringBuilder();
            var sbDiff = new StringBuilder();




            //if (modeDiff)
            //{
            //    histoDiff = null;
            //}
            //else
            //{
            //    if (histoDiff != null)
            //    {
            //        histo.AddRange(histoDiff);
            //        histoDiff = null;
            //    }

