            finally
            {
                //   gen.eraseGrouping(); 
                if (refresh != null)
                    refresh(PHASE2, EventArgs.Empty);

                detailClassementViewModel.IhmInDispo = ihmInDispo.none; 
                ExportCmd.CalculExport();
            }


