            get
            {
                if (currentTrack == null || currentClassList == null)
                    return null;
                var tracks = Album;
                if (tracks == null || tracks.Count == 0)
                    return null;
                return tracks.Find(t => t.PieceId == currentTrack.PieceId);
            }
            set
            {
            }
        }

        public void record()
        {

            if (currentTrack == null)
                return;
            if (uncheckAfter)
                currentTrack.Enabled = false;
            currentTrack.Classement = classement;
            Keyboard.ClearFocus();

            if (recordClassementObserver != null)
            {
                recordClassementObserver.signalClassementChange();
                if (auto)
                    recordClassementObserver.autoNextTrack(); 
            }

        }

        public bool CanRecord
        {
            get
            {
                return currentTrack != null && currentTrack.Enabled && isClassment(classement);
            }
        }

        public ICommand Record { get { return cmdRecord; } }


    }
}

using pdb.player.engine;
namespace pdb.player.ViewModel.Commande
{
    class PlayPause : PlayerCommande
    {

        public override bool CanExecute(object parameter)
        {
            if (player.State == playerstate.play)
                return true;
            return player.canPlay();
        }

        public override void Execute(object parameter)
        {
            if (player.isHidden)
                player.stop(); 
            if (player.State == playerstate.play)
                player.pause();
            else if (player.State == playerstate.pause)
                player.play(false,false);
            else
                player.play(false,false); 

            App.go(); 
        }
    }


    class Stop : PlayerCommande
    {

        public override bool CanExecute(object parameter)
        {
            return true;
        }

        public override void Execute(object parameter)
        {
            player.stop();
            App.go(); 
        }
    }

    class ForWard : PlayerCommande
    {
        public override bool CanExecute(object parameter)
        {
            return player.canForward(); 
        }

        public override void Execute(object parameter)
        {
            player.forward();
            var currentPlaying = TrackListViewModel.CurrentPlaying;
            if (currentPlaying != null)
                TrackListViewModel.HumanSelected = currentPlaying; 
            App.go(); 
        }
    }


    class BackWard : PlayerCommande
    {
        public override bool CanExecute(object parameter)
        {
            return player.canBackWard();
        }

        public override void Execute(object parameter)
        {
            player.backWard();
        }
    }




}

