        {
            var parent = " ";
            if (PieceParentId >= 0)
                parent = string.Format(" ({0}) ", PieceParentId);
            return string.Format("{0} {1}{2}{3} {4} {5}", master.getClassement(), PieceId, parent, Name, Album, master.File);
            //String location = Location;
            //return location == "" ? Files.File : location;
        }
