                {
                    try
                    {

                        PlayerViewModel.makeauto(this); 
                        App.bib.setClassement(this, value);



                        // piece.Piece.StrClassement = value;

                    }
                    catch
                    {
                    }
                    piece.Classement = value;
                    OnClassementChange(this);
                    // onPropertyChanged("Classement");


                }
                App.go();
            }
        }

