                    else
                    {
                        var cmp = new PieceExportComparer(this, source, next);
                        var l = source.List;
                        l.Sort(cmp);

                        var ll = new List<Piece>(l.Count);

                        trackSortedByExportPriority = new HashList<Piece>(l);


                    }



