using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using pdb.util;
using System.Xml;

namespace pdb.podcast.Tuning
{
    class Db : TuningBase
    {
        /// <summary>
        /// Suppression des éléments manquant (typiquement des podcasts supprimmés automatiquement genre journal
        /// </summary>
        public bool purgemissing;
        /// <summary>
        /// Chemin du fichier db
        /// </summary>
        public string file;
        /// <summary>
        /// Chemin du fichier de stats; 
        /// </summary>
        public string fileStat;
        /// <summary>
        /// Répertoire du fichier de sauvegarde
        /// </summary>
        public string sov;

        ///// <summary>
        ///// Chemin du fichier de stats cumulé
        ///// </summary>
        //public string globalStat;


        private List<DbSource> sources = new List<DbSource>(); 

        public Db()
        {
        }

        public Db(XmlElement node)
            : base(node)
        {
            if (node == null)
                return;
            buildb("purgemissing", v => purgemissing = v);
            builds("file", v => file = v);
            builds("fileStat", v => fileStat = v);
           // builds("globalStat", v => globalStat = v);
            builds("sov", v => sov = v);

            sources = new List<DbSource>(); 
        }
    }

    class DbSource : TuningBase
    {
        public string file;
        public DbSource()
        { }

        public DbSource(XmlElement node)
            : base(node)
        {
            if (node == null)
                return;
            builds("f", v => file = v);
        }
    }
}

using System.Collections.Generic;
using System.Xml;
using pdb.util;

namespace pdb.podcast.Tuning
{
    class RootIt : IEnumerable<RootItItem>
    {
        public List<RootItItem> roots = new List<RootItItem>();
        public List<OtherRoot> others = new List<OtherRoot>(); 
        public RootIt()
        {
            var item = new RootItItem();
            roots.Add(item); 
        }
        public string Root { get { return roots[0].name; } }

        public RootIt(XmlElement node) : this()
        {
            if (node == null)
                return;
            roots.Clear(); 
            var childs = node.ChildNodes;
            if (childs.Count == 0)
            {
                var item = new RootItItem();
                item.name = node.InnerText;
                roots.Add(item); 
            }
            else
            {
                foreach (XmlNode _xi in childs)
                {
                    if (_xi is XmlElement)
                    {
                        var xi = _xi as XmlElement;
                        if (xi.Name == "i")
                        {
                            var item = new RootItItem(node, xi);
                            roots.Add(item);
                        }
                        else if (xi.Name == "o")
                        {
                            var item = new OtherRoot(node, xi);
                            others.Add(item); 
                        }
                    }
                }
            }
        }

        public IEnumerator<RootItItem> GetEnumerator()
        {
            return roots.GetEnumerator(); 
        }

        System.Collections.IEnumerator System.Collections.IEnumerable.GetEnumerator()
        {
            return roots.GetEnumerator(); 
        }

    }
    class RootItItem : TuningBase
    {
        public string name = "auto";
        public double size = int.MaxValue;
        public RootItItem()
        {
        }

        public RootItItem(XmlElement parent, XmlElement child) : base(parent,child)
        {
            builds("name", v => name = v);
            buildd("size", v => size = v *1024*1024); 
        }
    }

    class OtherRoot : TuningBase
    {
        public string name = "auto";
        public double size = int.MaxValue;
        public OtherRoot()
        {
        }

        public OtherRoot(XmlElement parent, XmlElement child)
            : base(parent, child)
        {
            builds("name", v => name = v);
        }
    }
}
