        public String StrPlaying
        {
            get
            {
                if (playing) return "<))";
                return "";
            }
        }
        public string MedianneStatus
        {
            get
            {
                var p = PieceGen;
                if (p.Medianne)
                    return ">>"; 
                if (p.MedianneLight)
                    return ">";              
                if (p.BorderEffect)
                    return "--";
                if (p.Border)
                    return "-";
                return "";
            }
        }

