using System;
using pdb.db.piece;

namespace pdb.db.obj
{
    class CPieceChecked
    {
        private bool? disabled;

        public void Disable()
        {
            disabled = true;
            //if (!disabled)
            //{
            //    disabled = true;
            //    OnCheckedChanged();
            //}
        }
        private bool _override;

        public void Enable()
        {
            disabled = false;
        }

        public bool Enabled { get { return true != disabled; } }

        private IDataObserver observer;
        public IDataObserver Observer { set { observer = value; } }




        private void OnCheckedChanged()
        {
            if (observer != null) { observer.unCheck(); }
        }

        public void Consolid(CPieceChecked remote, mergePriority a_priority)
        {
            if (a_priority == mergePriority.right)
            {
                if (remote.disabled != null && remote.disabled != this.disabled)
                {
                    this.disabled = remote.disabled;
                    _override = true;
                }
                //if (remote.disabled)
                //{
                //    if (!this.disabled)
                //    {
                //        _override = true;
                //        this.disabled = true; 
                //    }
                //}
            }
            //else if (a_priority == mergePriority.left)
            //{
            //    if (disabled)
            //    {
            //        remote.Disable();
            //    }
            //}
        }

        public void LegacyConsolid(CPieceChecked local)
        {
            if (local._override)
            {
                if (local.disabled != null)
                {
                    if (local.disabled != this.disabled)
                    {
                        this.disabled = local.disabled;
                        OnCheckedChanged();
                    }
                }
                //if (true == local.disabled)
                //{
                //    if (true !=this.disabled)
                //    {
                //        this.disabled = true;
                //        OnCheckedChanged();
                //    }
                //}
                //else
                //    throw new ApplicationException("bizarre local est override mais pas désactivé");
            }
            else
            {
                if (this.disabled != null)
                    local.disabled = this.disabled;
            }

        }

        public void SynchroNative(bool enabled)
        {
            this.disabled = !enabled;
        }


    }
}
