        public void buildVirtualAlbums(List<Piece> list)
        {

            foreach (var path in track.RelativesPath)
            {

                var tab = path.Split('\\');
                var sb = new StringBuilder();

                sb.Append(@"\");
                var count = tab.GetLength(0);
                for (int i = 0; i < count; i++)
                {
                    var _s = tab[i];
                    if (string.IsNullOrEmpty(_s))
                        continue;

                    var virt = createVirtualAlbum(sb.ToString());

                    if (virt != null)
                        list.Add(virt);

                    if (i > 0)
                        sb.Append(@"\");
                    sb.Append(_s);




                }
            }
            string artiste = Artist;
            if (string.IsNullOrEmpty(artiste))
                return;
            if (artiste.Trim().Length < 2)
                return;
            if (CConf.ConfGen.ArtistVirtual)
            {
                var virtArtist = new VirtualAlbumPiece(track, Master, artiste.Trim(), true);
                list.Add(virtArtist);
            }

        }

        private Piece createVirtualAlbum(string path)
        {
            if (string.IsNullOrEmpty(path))
                return null;
            if (path.Length < 2)
                return null;
            var virt = new VirtualAlbumPiece(track, Master, path, false);
            return virt;
        }

