        cancel
    }
    class ExportCmd : CommandeBase
    {
        private static IExportTracks ihm;
        public static IExportTracks Ihm { set { ihm = value; } }
        // private static bool cancel;
        // protected static string dir;
       protected static PlayListViewModel lastpl;
