    public class Length
    {
        public double value;
        public GridUnitType unit;
        public Length(GridLength ihm)
        {
            value = ihm.Value;
            unit = ihm.GridUnitType;
        }
        public GridLength toGrid()
        {
            return new GridLength(value, unit);
        }
    }
