        private async Task<List<ITrackInfoItunes>> loadTracks(bool firstStep, bool forceLoad)
        {
            List<ITrackInfoItunes> listTrack = new List<ITrackInfoItunes>();
            await Task.Run(() =>
              {
                  loader.init();
                  loader.reset(firstStep, forceLoad);
                  loader.loadTracks(listTrack);
                  loader.loadLists();
              });
            return listTrack;

        }
        #endregion

